mapper.select:
select

from sys_user su
left join sys_dept sd on su.dept_id = sd.id
where su.tenant_id = #{tenantId}

    and su.username = #{user.username}


    and su.mobile = #{user.mobile}


    and su.status = #{user.status}


    and su.create_time >= #{user.startTime}


    and su.create_time <= #{user.endTime}
